<template>
  <div class="input__container">
    <div
      class="input__wrapper"
      ref="wrapper"
      :class="'input__wrapper--has-value input__wrapper--correct'"
      v-if="value !== null"
    >
      <div class="p-datepicker">
        <div class="p-datepicker-wrapper">
          <div class="p-datepicker__input-wrapper">
            <input
              class="input"
              ref="input"
              v-model="selectedDate"
              :name="inputName"
              :type="type"
              :value="value"
              :id="id"
              :required="required"
              :autocomplete="autocomplete"
              :maxlength="maxlength"
              disabled
              :class="{
                'input--disabled': disabled,
                'input--success': state === 'success',
                'input--error': state === 'error',
              }"
              @focus.stop="onFocus"
              @keydown="false"
            />
            <label :for="name" v-if="$slots.label" class="input__label">
              <span>
                <slot name="label"></slot>
                <span class="input__label-required" v-if="required">*</span>
              </span>
            </label>
            <a @click="selecting = !selecting">
              <svg
                width="23"
                height="23"
                viewBox="0 0 23 23"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
                xmlns:xlink="http://www.w3.org/1999/xlink"
              >
                <rect
                  width="23"
                  height="23"
                  fill="url(#pattern0)"
                  fill-opacity="0.7"
                />
                <defs>
                  <pattern
                    id="pattern0"
                    patternContentUnits="objectBoundingBox"
                    width="1"
                    height="1"
                  >
                    <use xlink:href="#image0" transform="scale(0.00195312)" />
                  </pattern>
                  <image
                    id="image0"
                    width="512"
                    height="512"
                    xlink:href="data:image/png;base64,"
                  />
                </defs>
              </svg>
            </a>
          </div>
          <div
            class="p-datepicker__dates"
            ref="pDatepickerDates"
            :class="{ 'p-datepicker__dates--open': selecting }"
          >
            <div class="p-datepicker__dates-header">
              <div class="p-datepicker__dates-prev p-datepicker__dates-btn">
                <a href="#" @click.prevent="!slideDisable ? slide('prev') : ''">
                  <svg
                    width="6"
                    height="8"
                    viewBox="0 0 6 8"
                    fill="none"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <rect
                      width="1.10932"
                      height="4.99192"
                      rx="0.554658"
                      transform="matrix(0.712539 0.701633 -0.712539 0.701633 4.50183 2.86719)"
                      fill="#FF5F54"
                    />
                    <rect
                      width="1.10932"
                      height="4.99192"
                      rx="0.554658"
                      transform="matrix(-0.712539 0.701633 -0.712539 -0.701633 5.29218 3.61816)"
                      fill="#FF5F54"
                    />
                  </svg>
                </a>
              </div>
              <div class="p-datepicker__dates-cur">
                <span>{{ monthWord }} {{ curYear }}</span>
              </div>
              <div class="p-datepicker__dates-next p-datepicker__dates-btn">
                <a href="#" @click.prevent="!slideDisable ? slide('next') : ''">
                  <svg
                    width="6"
                    height="8"
                    viewBox="0 0 6 8"
                    fill="none"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <rect
                      width="1.10932"
                      height="4.99192"
                      rx="0.554658"
                      transform="matrix(0.712539 0.701633 -0.712539 0.701633 4.50183 2.86719)"
                      fill="#FF5F54"
                    />
                    <rect
                      width="1.10932"
                      height="4.99192"
                      rx="0.554658"
                      transform="matrix(-0.712539 0.701633 -0.712539 -0.701633 5.29218 3.61816)"
                      fill="#FF5F54"
                    />
                  </svg>
                </a>
              </div>
            </div>
            <div class="p-datepicker__dates-body" ref="datesContainer">
              <div class="p-datepicker__dates-months">
                <ul>
                  <li v-for="(dayWeek, key) in weekDays" :key="key">
                    {{ dayWeek }}
                  </li>
                </ul>
              </div>
              <div
                class="
                  p-datepicker__dates-days p-datepicker__dates-days--cur-month
                "
                ref="datesDays"
              >
                <ul>
                  <li
                    v-for="(day, key) in prevMonthDaysPart"
                    :key="key + 'prev'"
                    class="p-datepicker__day--notCurMonth"
                  >
                    {{ day }}
                  </li>
                  <li
                    v-for="(day, key) in monthDays"
                    :key="key"
                    @click="pickDay(day)"
                    :class="[
                      day === curDay &&
                      curMonth === selectedMonth &&
                      curYear === selectedYear
                        ? 'p-datepicker__day--active'
                        : '',
                    ]"
                  >
                    <span>
                      {{ day }}
                    </span>
                  </li>
                  <li
                    v-for="(day, key) in nextMonthDaysPart"
                    :key="key + 'next'"
                    class="p-datepicker__day--notCurMonth"
                  >
                    {{ day }}
                  </li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
export default {
  name: "PCalendar",
  props: {
    delimiter: {
      type: String,
      required: false,
      default: ".",
    },
    allowMultiSelect: {
      type: Boolean,
      required: false,
      default: false,
    },
    value: {
      type: Date,
      required: false,
      default: new Date(),
    },
    name: {
      type: String,
      required: true,
    },
  },
  data() {
    return {
      selecting: false,
      selectedDate: "",
      mask: "DD" + this.delimiter + "MM" + this.delimiter + "YYYY",
      weekDays: ["M", "T", "W", "T", "F", "S", "S"],
      monthDays: [],
      prevMonthDays: [],
      nextMonthDays: [],
      prevMonthDaysPart: [],
      nextMonthDaysPart: [],
      firstWeekDay: 0,
      curMonth: 0,
      selectedMonth: 0,
      selectedYear: 0,
      date: false,
      curYear: 0,
      monthWord: "",
      curDay: 0,
      slideDisable: false,
      realMonth: 0,
      realYear: 0,
    };
  },
  mounted() {
    this.selectedDate = this.mask;
    this.date = new Date();
    this.value = new Date();
    this.curMonth = this.date.getMonth() + 1;
    this.curYear = this.date.getFullYear();
    this.curDay = this.date.getDate();
    this.realMonth = this.date.getMonth() + 1;
    this.realYear = this.date.getFullYear();
    this.setMostDates();
    this.monthWord = this.date.toLocaleString("en", { month: "long" });
    let mouseSlide = false;
    let coorXStart = 0;
    let coorXEnd = 0;
    document.addEventListener("mousedown", (e) => {
      if (e.target.closest(".p-datepicker__dates-days")) {
        mouseSlide = true;
        coorXStart = e.clientX;
      }
    });
    document.addEventListener("mouseup", (e) => {
      if (mouseSlide) {
        mouseSlide = false;
        coorXEnd = e.clientX;
        if (coorXStart - coorXEnd > 95) {
          this.slide("next");
        } else if (coorXEnd - coorXStart > 95) {
          this.slide("prev");
        }
        coorXStart = 0;
        coorXEnd = 0;
      } else {
        this.closeDates(e);
      }
    });
  },
  methods: {
    pickDay(day) {
      this.curDay = day;
      this.selectedMonth = this.curMonth;
      this.selectedYear = this.curYear;
      this.selectedDate = this.formatDate();
      this.changeDate();
      this.$emit("input", this.value);
    },
    changeDate() {
      this.value = new Date(this.curYear, this.curMonth - 1, this.curDay);
    },
    formatDate() {
      return (
        (this.curDay < 10 ? "0" + this.curDay : this.curDay) +
        this.delimiter +
        (this.curMonth < 10 ? "0" + this.curMonth : this.curMonth) +
        this.delimiter +
        this.curYear
      );
    },
    closeDates(e) {
      if (
        !e.target.closest(".p-datepicker__dates") &&
        this.selecting &&
        e.target.localName !== "rect" &&
        e.target.localName !== "svg" &&
        !e.target.closest(".p-datepicker__input-wrapper")
      ) {
        this.selecting = false;
        if (this.selectedMonth !== 0) {
          this.curMonth = this.selectedMonth;
          this.curYear = this.selectedYear;
        } else {
          this.curMonth = this.realMonth;
          this.curYear = this.realYear;
        }
      }
    },
    setMostDates() {
      const monthDays = new Date(this.curYear, this.curMonth - 1, 35);
      const prevMonthDays = new Date(this.curYear, this.curMonth - 2, 35);
      const nextMonthDays = new Date(this.curYear, this.curMonth, 35);
      const monthDaysAll = 35 - monthDays.getDate();
      const prevMonthDaysAll = 35 - prevMonthDays.getDate();
      const nextMonthDaysAll = 35 - nextMonthDays.getDate();
      const firstWeekDay = new Date(this.curYear, this.curMonth - 1, 1);
      this.firstWeekDay = firstWeekDay.getDay();
      let dayCicle = 1;
      let dayPrevMonthCicle = 1;
      let dayNextMonthCicle = 1;
      while (dayCicle <= monthDaysAll) {
        this.monthDays.push(dayCicle);
        dayCicle++;
      }
      while (dayPrevMonthCicle <= prevMonthDaysAll) {
        this.prevMonthDays.push(dayPrevMonthCicle);
        dayPrevMonthCicle++;
      }
      while (dayNextMonthCicle <= nextMonthDaysAll) {
        this.nextMonthDays.push(dayNextMonthCicle);
        dayNextMonthCicle++;
      }
      if (this.firstWeekDay > 1) {
        this.prevMonthDaysPart = this.prevMonthDays.slice(
          -this.firstWeekDay + 1
        );
      } else {
        this.prevMonthDaysPart = [];
      }
      const nextMonthDaysPartCount =
        42 - (this.prevMonthDaysPart.length + this.monthDays.length);
      this.nextMonthDaysPart = this.nextMonthDays.slice(
        0,
        nextMonthDaysPartCount
      );
    },
    slide(dir) {
      this.slideDisable = true;
      this.$refs.datesDays.classList.add("p-datepicker__dates-days--inAction");
      setTimeout(() => {
        this.slideDisable = false;
      }, 150);
      setTimeout(() => {
        this.$refs.datesDays.classList.remove(
          "p-datepicker__dates-days--inAction"
        );
      }, 200);
      if (dir === "prev") {
        this.curMonth--;
        this.$refs.datesDays.classList.add(
          "p-datepicker__dates-days--prevSlide"
        );
        setTimeout(() => {
          this.$refs.datesDays.classList.remove(
            "p-datepicker__dates-days--prevSlide"
          );
        }, 150);
      } else if (dir === "next") {
        this.curMonth++;
        this.$refs.datesDays.classList.add(
          "p-datepicker__dates-days--nextSlide"
        );
        setTimeout(() => {
          this.$refs.datesDays.classList.remove(
            "p-datepicker__dates-days--nextSlide"
          );
        }, 150);
      }
    },
  },
  watch: {
    curMonth: function (val) {
      if (val === 0) {
        this.curMonth = 12;
        this.curYear--;
      } else if (val === 13) {
        this.curMonth = 1;
        this.curYear++;
      }
      this.monthDays = [];
      this.monthDaysPrevSlide = [];
      this.prevMonthDays = [];
      this.nextMonthDays = [];
      this.setMostDates();
      this.changeDate();
      this.monthWord = this.value.toLocaleString("en", { month: "long" });
    },
  },
};
</script>

<style src="./style.scss" lang="scss"></style>
